"""We can transform these two to a more useful general function."""

def yfin_singleplot(symbol, length, tinterval, price, title, ticks):
  stock = yf.Ticker(symbol)
  data = stock.history(period = length, interval = tinterval)
  x = data.index
  y1 = data[price]
  fig, ax = plt.subplots()
  plot(x, y1, ax, title, 'Years','Price (Dollars)')
  ax.xaxis.set_major_locator(plt.MaxNLocator(ticks))
  return fig

figure = yfin_singleplot('TSLA', '1y', '1d', 'Open', 'Opening Price of Tesla Stocks', 5)

